import React from 'react';
import './App.css';
import { SearchBar } from '../SearchBar/SearchBar.js';
import { SearchResults } from '../SearchResults/SearchResults.js';
import { Playlist } from '../Playlist/Playlist.js';

class App extends React.Component {
constructor(props) {
  super(props);
  this.state = {
    searchResults: [{name: 'foo', artist: 'bar', album: 'foobar', id: '1'},{name: 'foo', artist: 'kdhjhhh', album: 'slkfjslkfj', id: 2}],
    playlistName: 'super geile playlist',
    playlistTracks: [{name: 'foofighter', artist: 'poobar', album: 'flloobar', id: '3'},{name: 'poofoo', artist: 'pppkdhjhhh', album: 'huzz', id: 4}, {name: 'gell', artist: 'boa', album: 'muau', id: '5'},{name: 'mmmm', artist: 'käsnudl', album: 'keiser', id: 6}]
  };
  this.addTrack = this.addTrack.bind(this);
  this.removeTrack = this.removeTrack.bind(this);
}
addTrack(track) {
  if (!this.state.playlistTracks.some((object) => object.id === track.id)) {
    const newPlaylist = this.state.playlistTracks;
    newPlaylist.push(track);
    this.setState({ playlistTracks: newPlaylist });
  } else {
    return
  }
}
removeTrack(track) {
  let newPlaylist = this.state.playlistTracks;
  console.log(newPlaylist);
  newPlaylist = newPlaylist.filter(song => song.id !== track.id);
  this.setState({playlistTracks: newPlaylist});
  console.log(this.state.playlistTracks);


}
render() {
  return (
    <div>
      <h1>Ja<span className="highlight">mmm</span>ing</h1>
      <div className="App">
        <SearchBar/>
        <div className="App-playlist">
          <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack}/>
          <Playlist playlistName={this.state.playlistName} playlistTracks={this.state.playlistTracks} onRemove={this.removeTrack}/>
        </div>
      </div>
    </div>
  );
}
}

export default App;
